mapper.select:
select m.id,m.title,m.content,m.view_time viewTime,m.end_time endTime,m.create_date createDate,(view_time < now() and now() < end_time) isLive ,l.id lawyerId,l.name lawyerName,l.photo_url photoUrl
,(select count(*) from microview_attention ma where ma.interview_id = m.id and lawyer_id=#{lawyerId}) isAtten
from microview m inner join lawyer l on m.lawyer_id = l.id limit #{startCount},#{pageSize}

select d.id,d.type,d.content,d.create_date createDate ,l.id lawyerId,l.name lawyerName,l.photo_url photoUrl ,(select count(*) from microview_praise mp where mp.discuss_id = d.id) praiseCount
,(select count(*) from microview_praise mp where mp.discuss_id = d.id and mp.lawyer_id = #{lawyerId}) isParise
from microview_discuss d inner join lawyer l on l.id = d.lawyer_id where d.interview_id = #{microViewId} limit #{startCount},#{pageSize}
